import {Component, Input, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {error} from "@angular/compiler-cli/src/transformers/util";
const shajs = require('sha.js');
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  error : string = '';
  ngOnInit() {
    /*this.rou.queryParams.subscribe((params:any)=>{
      console.log(params.data2);
      if (params.data2==="false"){
        // @ts-ignore
        document.getElementById("log").style.display = "block";
      }
      else {
        // @ts-ignore
        document.getElementById("log").style.display = "none";
      }
    });*/
  }

  constructor(
      private router :Router,private rou:ActivatedRoute
  ) {
  }

  register(){
    this.router.navigate(["/nav-bar/"],{queryParams:{data1:"false"}});
    // @ts-ignore
    document.getElementById("log").style.display = "none";
  }
  login() {
    let email = (document.getElementById('email') as HTMLInputElement | null)?.value;
    let pass = (document.getElementById('pass') as HTMLInputElement | null)?.value;
    if (email===null||email===''){
      this.error = "please enter an email";
    }
    else if(pass===null||pass===''){
      this.error = "please enter a password";
    }
    else{
      pass = shajs('sha256').update(pass).digest('hex');
      let pdata = [email,pass];

    }
  }
}
